import { Canvas, Timeline, Children } from '@antv/f2';

const context = document.getElementById('container').getContext('2d');

//#region 测试数据
// icons
const icons = {
  imgRunBase64:
    'data:image/png;base64,',
  imgWalkBase64:
    'data:image/png;base64,',
  imgStandBase64:
    'data:image/png;base64,',
};

// 数据集
let data = [];
const control = (n, threshold = 0.3) => {
  if (n < threshold) {
    return n + 0.15;
  }
  if (n > 1 - threshold) {
    return n - 0.15;
  }
  return n;
};
const produceData = (count) => {
  for (let i = 0; i < count; i++) {
    data[i] = [
      {
        cate: 'stand',
        time: control(Math.random()).toFixed(1),
        color: '#e90b3a',
        bgColor: '#40131d',
        icon: icons.imgStandBase64,
      },
      {
        cate: 'walk',
        time: control(Math.random()).toFixed(1),
        color: '#a0ff03',
        bgColor: '#324214',
        icon: icons.imgWalkBase64,
      },
      {
        cate: 'run',
        time: control(Math.random()).toFixed(1),
        color: '#1ad5de',
        bgColor: '#183C3D',
        icon: icons.imgRunBase64,
      },
    ];
  }
  return data;
};
data = produceData(8);
//#endregion

/**
 * 设置
 */
const rUnit = 20;
const gap = 1;
const bgArcWidth = rUnit - gap;
const arcWidth = bgArcWidth - gap;

//#region 图表
// 外壳组件
const Shell = (props, context) => {
  const { children, data } = props;
  const { left, top, width, height } = context;
  const centerX = left + width / 2;
  const centerY = top + height / 2;
  return Children.map(children, (child) => {
    return Children.cloneElement(child, {
      data,
      centerX,
      centerY,
      rUnit,
    });
  });
};

// 背景组件
const ArcGuideGroup = (props) => {
  const { attrs } = props;
  const { data, centerX, centerY, rUnit } = props;
  return (
    <group>
      {data.map((record, index) => {
        return (
          <arc
            key={'arc_' + index}
            attrs={{
              ...attrs,
              x: centerX,
              y: centerY,
              r: rUnit * (1 * index + 1),
              stroke: record.bgColor,
            }}
          />
        );
      })}
    </group>
  );
};

// 弧线组件
const RoundArcGroup = (props) => {
  const { data, centerX, centerY, rUnit, attrs, animation } = props;
  return (
    <group>
      {data.map((record, index) => {
        return (
          <arc
            key={'record_' + index}
            attrs={{
              ...attrs,
              x: centerX,
              y: centerY,
              r: (1 * index + 1) * rUnit,
              stroke: record.color,
              startAngle: Math.PI / -2,
              endAngle: record.time * 2 * Math.PI - Math.PI / 2,
            }}
            animation={{ ...animation }}
          />
        );
      })}
    </group>
  );
};

// icon组件
const ImgGuideGroup = (props) => {
  const { data, centerX, centerY, rUnit, attrs } = props;
  const { width, height } = attrs;
  return (
    <group>
      {data.map((record, index) => {
        return (
          <image
            cacheImage
            key={'img_' + index}
            attrs={{
              ...attrs,
              width,
              height,
              x: centerX - width / 2,
              y: centerY - (1 * index + 1) * rUnit - height / 2,
              src: record.icon,
            }}
          />
        );
      })}
    </group>
  );
};

// apple-watch
const { props } = (
  <Canvas context={context} pixelRatio={window.devicePixelRatio}>
    <Timeline loop delay={0}>
      {Object.keys(data).map((iter) => {
        const records = data[iter];
        return (
          <Shell key={'shell_' + iter} context={context} data={records}>
            <ArcGuideGroup
              attrs={{
                lineWidth: bgArcWidth,
                opacity: 0.9,
              }}
            />
            <RoundArcGroup
              attrs={{
                lineWidth: arcWidth,
                lineCap: 'round',
                shadowColor: 'rgba(0,0,0,0.8)',
                shadowOffsetX: -2,
                shadowOffsetY: -5,
                shadowBlur: 50,
              }}
              animation={{
                appear: {
                  duration: 1500,
                  easing: 'elasticOut',
                  property: ['endAngle'],
                },
                update: {
                  duration: 1500,
                  easing: 'elasticOut',
                  property: ['endAngle'],
                },
              }}
            />
            <ImgGuideGroup
              attrs={{
                width: 16,
                height: 16,
              }}
            />
          </Shell>
        );
      })}
    </Timeline>
  </Canvas>
);

//#endregion

const canvas = new Canvas(props);
canvas.render();
