    label: function label(text, index, total) {
      var cfg = {
        textAlign: 'center',
        fill: '#85A5FF',
        fontSize: 12,
        fontWeight: 300
      };

      if (index === 0) {
        cfg.textAlign = 'start';
        cfg.text = text.split('-').join('月');
      } else {
        cfg.text = text.split('-')[1];
      }

      if (index === total - 1) {
        cfg.textAlign = 'end';
        cfg.fill = '#ADC6FF';
        cfg.fontWeight = 'normal';
      }

      return cfg;
    }
  });
  chart.axis('steps', {
    position: 'right',
    label: {
      fill: '#CFFFFE',
      fillOpacity: 0.5,
      fontSize: 9,
      fontWeight: 300
    },
    grid: {
      stroke: '#85A5FF'
    }
  }); // 设置tooltip

  chart.tooltip({
    custom: true,
    showCrosshairs: true,
    showTooltipMarker: false,
    crosshairsStyle: {
      lineDash: [2],
      stroke: '#77C0B3'
    },
    onChange: function onChange(e) {
      var item = e.items[0];
      var origin = item.origin;
      var tooltipEl = $('#tooltip');
      tooltipEl.text(origin.steps); // 设置 tooltip 位置

      var canvasOffsetTop = $('#container').position().top;
      var canvasOffsetLeft = $('#container').position().left;
      var tooltipWidth = tooltipEl.outerWidth();
      tooltipEl.css({
        visibility: 'visible',
        left: canvasOffsetLeft + item.x - tooltipWidth / 2,
        top: canvasOffsetTop
      });
    },
    onHide: function onHide() {
      var tooltipEl = $('#tooltip');
      tooltipEl.css({
        visibility: 'hidden'
      });
    }
  }); // 分别绘制area、折线和点

  chart.area().position('date*steps').style({
    fill: 'l(-90) 0.03:rgba(216,216,216,0.10) 1:#6E6CD8',
    fillOpacity: 1
  });
  chart.line().position('date*steps').color('#EFDBFF').size(1.5);
  chart.point().position('date*steps').color('#EED5FF').size(2.5);
  chart.render();
});

