import { jsx, Canvas, Chart, Area, Line, Axis, Tooltip } from '@antv/f2';

const context = document.getElementById('container').getContext('2d');

const data = [{
  month: 'Jan.',
  value: 6.06
}, {
  month: 'Feb.',
  value: 82.2
}, {
  month: 'Mar.',
  value: -22.11
}, {
  month: 'Apr.',
  value: 21.53
}, {
  month: 'May.',
  value: -21.74
}, {
  month: 'Jun.',
  value: 73.61
}, {
  month: 'Jul.',
  value: 53.75
}, {
